AND visible IN
#{item}

        SELECT id, IF(is_from_sender = #{isSender}, 1, 0) as is_me, content, time, is_read,
        room_id,
        visible
        FROM dm
        WHERE room_id = #{room_id}

            AND visible IN

                #{item}


--         ORDER BY time DESC
