mapper.update:
UPDATE dm
SET is_read = true
WHERE room_id = #{room_id}
  AND is_from_sender = #{is_from_sender}
  AND is_read = false

UPDATE room
SET sender_unread_count = 0
WHERE id = #{room_id}

UPDATE room
SET receiver_unread_count = 0
WHERE id = #{room_id}

UPDATE dm
SET visible = #{newVisible}
WHERE room_id = #{room_id}
  AND visible = #{oldVisible}

