mapper.update:
UPDATE poll_user
SET poll_option = #{pollOption}
WHERE user_id = #{userId}
  and poll_id = #{pollId};

mapper.select:
SELECT poll_option
FROM poll_user
WHERE user_id = #{userId}
  and poll_id = #{pollId};

mapper.update:
UPDATE poll_option
SET option_cnt = option_cnt - 1
WHERE id = #{optionId}
  and poll_id = #{pollId};

UPDATE poll_option
SET option_cnt = option_cnt + 1
WHERE id = #{optionId}
  and poll_id = #{pollId};
