mapper.select:
SELECT *
FROM room
WHERE sender_id = #{sender_id}
  AND receiver_id = #{receiver_id}

mapper.update:
UPDATE room
SET last_content = #{content}, receiver_unread_count = receiver_unread_count+1
WHERE id = #{room_id}

UPDATE room
SET last_content = #{content}, sender_unread_count = sender_unread_count+1
WHERE id = #{room_id}

UPDATE room
SET sender_is_deleted = false
WHERE id = #{room_id}

UPDATE room
SET receiver_is_deleted = false
WHERE id = #{room_id}

UPDATE room
SET time = #{time}
WHERE id = #{room_id}

UPDATE room
SET sender_dm_cursor = (
    SELECT MAX(id) + 1
    FROM dm
    WHERE room_id = #{room_id}
)
WHERE id = #{room_id}

UPDATE room
SET receiver_dm_cursor = (
    SELECT MAX(id) + 1
    FROM dm
    WHERE room_id = #{room_id}
    )
WHERE id = #{room_id}
