mapper.select:
SELECT b.id AS boardId, title, SUBSTRING(b.body, 1, 30) as body, b.created_at AS createdAt, b.modified_at AS modifiedAt, IF(is_anonymous, '익명',u.nickname) AS userNickname, comment_cnt AS commentCnt, like_cnt AS likeCnt, is_anonymous AS isAnonymous, is_hide AS isHide, IF(blike.user_id IS NOT NULL, 1, 0) as isLike, b.images_url as imagesUrlString, GROUP_CONCAT(t.name ORDER BY t.name ASC SEPARATOR ',') as tagStrings, type_id AS typeId, bt.name AS type FROM board b INNER JOIN ( SELECT swi.board_id, SUM(swi.tf * swi.idf) AS total_tfidf_value FROM search_word_inverse swi JOIN search_word n ON swi.search_word_id = n.id WHERE search_word.word IN
#{word}
GROUP BY swi.board_id ) AS tfidf ON b.id = tfidf.board_id INNER JOIN user u ON b.user_id = u.id INNER JOIN board_type bt ON b.type_id = bt.id LEFT OUTER JOIN board_like blike ON b.id = blike.board_id AND blike.user_id = #{userId} LEFT OUTER JOIN board_tag ON b.id = board_tag.board_id LEFT OUTER JOIN tag t ON board_tag.tag_id = t.id WHERE is_hide = 0 AND b.state = 0 AND b.category_id = #{categoryId}
AND b.type_id = #{boardTypeId}
GROUP BY b.id ORDER BY tfidf.total_tfidf_value DESC LIMIT #{pagingDto.recordSize} OFFSET #{pagingDto.offset}

SELECT COUNT(*) FROM board b INNER JOIN ( SELECT swi.board_id FROM search_word_inverse swi INNER JOIN search_word n ON swi.search_word_id = n.id WHERE search_word.word IN
#{word}
GROUP BY swi.board_id ) AS tfidf ON b.id = tfidf.board_id WHERE is_hide = 0 AND b.state = 0 AND b.category_id = #{categoryId}
AND b.type_id = #{boardTypeId}
